INSERT INTO `stretchy_parameter` (`id`, `parameter_name`, `parameter_label`, `parameter_displayType`, `parameter_FormatType`, `parameter_default`, `special`, `parameter_sql`) VALUES (1004, 'FullReportDetails', 'n/a', 'n/a', 'n/a', 'n/a', 'Y', 'select  r.id as report_id, r.report_name, r.report_type, r.report_subtype, r.report_category,\r\nrp.id as parameter_id, rp.report_parameter_name, p.parameter_name\r\n  from stretchy_report r\r\n  left join stretchy_report_parameter rp on rp.report_id = r.id \r\n  left join stretchy_parameter p on p.id = rp.parameter_id\r\n  where r.use_report is true\r\n  and r.report_name = \'${reportName}\'\r\n  and exists\r\n  ( select \'f\'\r\n  from m_appuser_role ur \r\n  join m_role r on r.id = ur.role_id\r\n  join m_role_permission rp on rp.role_id = r.id\r\n  join m_permission p on p.id = rp.permission_id\r\n  where ur.appuser_id = 1\r\n  and (p.code in (\'ALL_FUNCTIONS_READ\', \'ALL_FUNCTIONS\') or p.code = concat("READ_", r.report_name)) )\r\n  order by r.report_category, r.report_name, rp.id');